// src/components/Cars/CarUploadForm.js
import React, { useState } from 'react';
import axios from 'axios';

const CarUploadForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    brand: '',
    model: '',
    year: '',
    price: '',
    paymentType: 'cash',
    description: '',
    image: null,
  });

  const [message, setMessage] = useState('');

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token'); // or from context
      const data = new FormData();
      Object.entries(formData).forEach(([key, value]) => data.append(key, value));

      const res = await axios.post('http://localhost:5000/api/cars', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      setMessage('✅ Car uploaded successfully!');
    } catch (err) {
      console.error(err);
      setMessage('❌ Error uploading car');
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto bg-white shadow-lg rounded">
      <h2 className="text-2xl font-bold mb-4">Sell Your Car</h2>
      <form onSubmit={handleSubmit} className="grid gap-4">
        <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
        <input type="text" name="brand" placeholder="Brand" onChange={handleChange} required />
        <input type="text" name="model" placeholder="Model" onChange={handleChange} required />
        <input type="number" name="year" placeholder="Year" onChange={handleChange} required />
        <input type="number" name="price" placeholder="Price" onChange={handleChange} required />

        <select name="paymentType" onChange={handleChange} required>
          <option value="cash">Cash</option>
          <option value="installment">Installment</option>
        </select>

        <textarea name="description" placeholder="Description" onChange={handleChange} required></textarea>

        <input type="file" name="image" accept="image/*" onChange={handleChange} required />

        <button type="submit" className="bg-blue-600 text-white py-2 rounded">Upload Car</button>
      </form>
      {message && <p className="mt-4 text-center">{message}</p>}
    </div>
  );
};

export default CarUploadForm;
