// frontend/src/components/Cars/EditCar.js
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../../utils/api";

const EditCar = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [car, setCar] = useState({
    title: "",
    brand: "",
    model: "",
    year: "",
    price: "",
    paymentType: "cash",
    description: ""
  });

  useEffect(() => {
    const fetchCar = async () => {
      try {
        const res = await API.get(`/cars/${id}`);
        console.log("Car data:", res.data); // Debug log
        setCar(res.data);
      } catch (err) {
        console.error("Error fetching car:", err);
        alert("Failed to load car details");
      }
    };
    fetchCar();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.put(`/cars/${id}`, car);
      navigate("/user-dashboard");
    } catch (err) {
      console.error("Update error:", err);
      alert("Failed to update car: " + (err.response?.data?.message || err.message));
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCar(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="edit-car-container">
      <h2>Edit Car Listing</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields similar to CarUploadForm */}
      </form>
    </div>
  );
};

export default EditCar;
