// src/components/Navbar.js
import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "../styles/Navbar.css";

const Navbar = () => {
  const { user } = useContext(AuthContext);

  return (
    <nav className="navbar">
      <div className="nav-content">
        <Link to="/" className="nav-logo">AutoTrust</Link>
        <div className="nav-links">
          <Link to="/" className="nav-btn">Cars</Link>

          {/* Only sellers can upload cars */}
          {user?.role === "seller" && (
            <Link to="/upload-car" className="nav-btn">Upload Car</Link>
          )}

          {/* Show login/register if not logged in */}
          {!user && (
            <>
              <Link to="/login" className="nav-btn">Login</Link>
              <Link to="/register" className="nav-btn">Register</Link>
            </>
          )}

          {/* Show logout if logged in */}
          {user && (
            <Link to="/logout" className="nav-btn logout">Logout</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
