import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";

const UserDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const [userCars, setUserCars] = useState([]);
  const [formData, setFormData] = useState({ name: "", email: "", password: "" });

  // Fetch user's cars
  useEffect(() => {
    const fetchUserCars = async () => {
      try {
        const res = await API.get("/user/my-cars");
        setUserCars(res.data);
      } catch (err) {
        alert("Failed to load your cars.");
      }
    };
    fetchUserCars();
  }, []);

  // Handle profile update
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await API.put("/user", formData);
      alert("Profile updated!");
    } catch (err) {
      alert("Update failed.");
    }
  };

  // Handle account deletion
  const handleDelete = async () => {
    if (window.confirm("Delete your account permanently?")) {
      try {
        await API.delete("/user");
        logout();
      } catch (err) {
        alert("Deletion failed.");
      }
    }
  };

  return (
    <div className="dashboard">
      <h2>Your Account</h2>

      {/* Profile Update Form */}
      <form onSubmit={handleUpdate}>
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
        />
        <input
          type="password"
          placeholder="New Password (leave blank to keep current)"
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
        />
        <button type="submit">Update Profile</button>
      </form>

      {/* Listed Cars */}
      <h3>Your Cars ({userCars.length})</h3>
      <div className="car-grid">
        {userCars.map((car) => (
          <div key={car._id} className="car-card">
            <img src={car.image} alt={car.title} />
            <h4>{car.title}</h4>
            <Link to={`/edit-car/${car._id}`}>Edit</Link> {/* Add EditCar.js later */}
          </div>
        ))}
      </div>

      {/* Delete Account Button */}
      <button onClick={handleDelete} className="delete-btn">
        Delete Account
      </button>
    </div>
  );
};

export default UserDashboard;
