import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";
import "../../styles/Dashboard.css";

const UserDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const [userCars, setUserCars] = useState([]);
  const [profile, setProfile] = useState({ name: "", email: "" });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [profileRes, carsRes] = await Promise.all([
          API.get("/user/profile"),
          API.get("/user/my-cars")
        ]);
        setProfile(profileRes.data);
        setUserCars(carsRes.data);
      } catch (err) {
        console.error("Failed to load data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await API.put("/user", profile);
      alert("Profile updated successfully!");
    } catch (err) {
      alert("Update failed: " + (err.response?.data?.message || err.message));
    }
  };

  if (loading) return <div className="dashboard-loading">Loading...</div>;

  return (
    <div className="dashboard-container">
      <div className="profile-section">
        <h2>Your Profile</h2>
        <form onSubmit={handleUpdate}>
          <input
            type="text"
            value={profile.name}
            onChange={(e) => setProfile({...profile, name: e.target.value})}
          />
          <input
            type="email"
            value={profile.email}
            onChange={(e) => setProfile({...profile, email: e.target.value})}
          />
          <button type="submit">Update Profile</button>
        </form>
      </div>

      <div className="cars-section">
        <h3>Your Listings ({userCars.length})</h3>
        {userCars.length > 0 ? (
          <div className="user-cars-grid">
            {userCars.map((car) => (
              <div key={car._id} className="user-car-card">
                <img src={car.image} alt={car.title} />
                <h4>{car.title}</h4>
                <p>${car.price}</p>
                <button onClick={() => navigate(`/edit-car/${car._id}`)}>
                  Edit
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p>You haven't listed any cars yet.</p>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
