// src/components/Cars/CarList.js
import React, { useEffect, useState, useContext } from "react";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";

const CarList = () => {
  const [cars, setCars] = useState([]);
  const { user } = useContext(AuthContext); // Get current user from AuthContext

  const fetchCars = async () => {
    try {
      const res = await API.get("/cars");
      setCars(res.data);
    } catch (err) {
      console.error("Failed to fetch cars:", err);
    }
  };

  const handleDelete = async (carId) => {
    try {
      await API.delete(`/cars/${carId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      alert("Car deleted successfully!");
      fetchCars(); // Refresh the list after deletion
    } catch (err) {
      console.error("Failed to delete car:", err);
      alert("You can only delete your own cars!");
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  return (
    <div>
      <h2>Available Cars</h2>
      {cars.length === 0 ? (
        <p>No cars found</p>
      ) : (
        cars.map((car) => (
          <div key={car._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px" }}>
            <h3>{car.title}</h3>
            <p>Brand: {car.brand} | Price: ${car.price}</p>
            {car.image && <img src={car.image} alt={car.title} width="200" />}
            
            {/* Show delete button ONLY to the car owner or admin */}
            {(user?.id === car.seller?._id || user?.isAdmin) && (
              <button 
                onClick={() => handleDelete(car._id)} 
                style={{ background: "red", color: "white" }}
              >
                Delete
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default CarList;
