import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";
import "../../styles/Dashboard.css";

const UserDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const [userCars, setUserCars] = useState([]);
  const [profile, setProfile] = useState({ 
    name: "", 
    email: "",
    currentPassword: "",
    newPassword: "",
    confirmPassword: ""
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [profileRes, carsRes] = await Promise.all([
          API.get("/user/profile"),
          API.get("/user/my-cars")
        ]);
        setProfile({
          ...profileRes.data,
          currentPassword: "",
          newPassword: "",
          confirmPassword: ""
        });
        setUserCars(carsRes.data);
      } catch (err) {
        console.error("Failed to load data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const validateForm = () => {
    const newErrors = {};

    if (!profile.name) newErrors.name = "Name is required";
    if (!profile.email) {
      newErrors.email = "Email is required";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(profile.email)) {
      newErrors.email = "Invalid email format";
    }

    if (profile.newPassword && !profile.currentPassword) {
      newErrors.currentPassword = "Current password is required to change password";
    }

    if (profile.newPassword && profile.newPassword.length < 6) {
      newErrors.newPassword = "Password must be at least 6 characters";
    }

    if (profile.newPassword !== profile.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    setSuccessMessage("");

    if (!validateForm()) return;

    try {
      const updateData = {
        name: profile.name,
        email: profile.email,
        ...(profile.newPassword && {
          currentPassword: profile.currentPassword,
          newPassword: profile.newPassword
        })
      };

      await API.put("/user", updateData);
      setSuccessMessage("Profile updated successfully!");

      // Clear password fields after successful update
      setProfile(prev => ({
        ...prev,
        currentPassword: "",
        newPassword: "",
        confirmPassword: ""
      }));
    } catch (err) {
      const errorMsg = err.response?.data?.message || "Update failed";
      alert(errorMsg);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ""
      }));
    }
  };

  // ... rest of the component (car listing and deletion logic remains the same) ...

  return (
    <div className="dashboard-container">
      <div className="profile-section">
        <h2>Your Profile</h2>

        {successMessage && (
          <div className="success-message">{successMessage}</div>
        )}

        <form onSubmit={handleUpdate} className="profile-form">
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              id="name"
              type="text"
              name="name"
              value={profile.name}
              onChange={handleChange}
              className={errors.name ? "error" : ""}
            />
            {errors.name && <span className="error-message">{errors.name}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              name="email"
              value={profile.email}
              onChange={handleChange}
              className={errors.email ? "error" : ""}
            />
            {errors.email && <span className="error-message">{errors.email}</span>}
          </div>

          <div className="password-section">
            <h4>Change Password</h4>

            <div className="form-group">
              <label htmlFor="currentPassword">Current Password</label>
              <input
                id="currentPassword"
                type="password"
                name="currentPassword"
                value={profile.currentPassword}
                onChange={handleChange}
                placeholder="Leave blank to keep current password"
                className={errors.currentPassword ? "error" : ""}
              />
              {errors.currentPassword && (
                <span className="error-message">{errors.currentPassword}</span>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="newPassword">New Password</label>
              <input
                id="newPassword"
                type="password"
                name="newPassword"
                value={profile.newPassword}
                onChange={handleChange}
                className={errors.newPassword ? "error" : ""}
              />
              {errors.newPassword && (
                <span className="error-message">{errors.newPassword}</span>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm New Password</label>
              <input
                id="confirmPassword"
                type="password"
                name="confirmPassword"
                value={profile.confirmPassword}
                onChange={handleChange}
                className={errors.confirmPassword ? "error" : ""}
              />
              {errors.confirmPassword && (
                <span className="error-message">{errors.confirmPassword}</span>
              )}
            </div>
          </div>

          <button type="submit" className="update-btn">
            Update Profile
          </button>
        </form>
      </div>

      {/* Car listing section remains the same */}
    </div>
  );
};

export default UserDashboard;
