import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";
import "../styles/AuthPages.css";

const CarList = () => {
  const [cars, setCars] = useState([]);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    axios.get("/api/cars")
      .then(res => setCars(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="auth-page car-list-bg">
      <div className="car-list-container">
        <h2>Available Cars</h2>
        <div className="car-grid">
          {cars.map(car => (
            <div className="car-card" key={car._id}>
              <img src={car.imageUrl} alt={car.title} className="car-img" />
              <h3>{car.title}</h3>
              <p>Price: ${car.price}</p>
              {car.installment && <p>Installment Option Available</p>}
              <Link to={`/car/${car._id}`} className="nav-button">View Details</Link>
              {(user?.id === car.seller || user?.isAdmin) && (
                <button className="nav-button delete-btn">Delete</button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CarList;
