// src/components/Cars/CarList.js
import React, { useEffect, useState, useContext } from "react";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";

const CarList = () => {
  const [cars, setCars] = useState([]);
  const { user } = useContext(AuthContext);

  const fetchCars = async () => {
    try {
      const res = await API.get("/cars");
      setCars(res.data);
    } catch (err) {
      console.error("Failed to fetch cars:", err);
      alert("Failed to load cars. Please try again later.");
    }
  };

  const handleDelete = async (carId) => {
    // Confirm before deleting
    if (!window.confirm("Are you sure you want to delete this car?")) {
      return;
    }

    try {
      await API.delete(`/cars/${carId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      alert("Car deleted successfully!");
      fetchCars(); // Refresh the list after deletion
    } catch (err) {
      console.error("Failed to delete car:", err);
      alert(
        err.response?.data?.message || 
        "You can only delete your own cars or there was an error."
      );
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  return (
    <div className="car-list-container">
      <h2>Available Cars</h2>
      {cars.length === 0 ? (
        <p>No cars found</p>
      ) : (
        cars.map((car) => (
          <div 
            key={car._id} 
            className="car-card"
            style={{ 
              border: "1px solid #ccc", 
              padding: "10px", 
              margin: "10px",
              position: "relative" 
            }}
          >
            <h3>{car.title}</h3>
            <p>Brand: {car.brand} | Price: ${car.price}</p>
            {car.image && (
              <img 
                src={car.image} 
                alt={car.title} 
                style={{ 
                  width: "200px", 
                  height: "auto",
                  margin: "10px 0" 
                }} 
              />
            )}
            
            {/* Show delete button ONLY to the car owner or admin */}
            {(user?.id === car.seller?._id || user?.isAdmin) && (
              <button 
                onClick={() => handleDelete(car._id)}
                style={{ 
                  background: "red", 
                  color: "white",
                  padding: "5px 10px",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  position: "absolute",
                  top: "10px",
                  right: "10px"
                }}
              >
                Delete
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default CarList;
