import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";
import "../../styles/Dashboard.css";

const UserDashboard = () => {
  const { user } = useContext(AuthContext);
  const [userCars, setUserCars] = useState([]);
  const [profile, setProfile] = useState({ 
    name: "", 
    email: "",
    currentPassword: "",
    newPassword: "",
    confirmPassword: ""
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");
  const [activeTab, setActiveTab] = useState("profile"); // 'profile' or 'cars'
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [profileRes, carsRes] = await Promise.all([
          API.get("/user/profile"),
          API.get("/user/my-cars")
        ]);
        setProfile({
          ...profileRes.data,
          currentPassword: "",
          newPassword: "",
          confirmPassword: ""
        });
        setUserCars(carsRes.data);
      } catch (err) {
        console.error("Failed to load data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Profile update functions remain the same...

  const handleDeleteCar = async (carId) => {
    if (window.confirm("Are you sure you want to delete this car listing?")) {
      try {
        await API.delete(`/cars/${carId}`);
        setUserCars(userCars.filter(car => car._id !== carId));
        setSuccessMessage("Car listing deleted successfully");
      } catch (err) {
        alert("Failed to delete car: " + (err.response?.data?.message || err.message));
      }
    }
  };

  const handleApproveCar = async (carId) => {
    try {
      await API.patch(`/cars/${carId}/approve`);
      setUserCars(userCars.map(car => 
        car._id === carId ? {...car, isApproved: true} : car
      ));
      setSuccessMessage("Car approved successfully");
    } catch (err) {
      alert("Approval failed: " + (err.response?.data?.message || err.message));
    }
  };

  if (loading) return <div className="dashboard-loading">Loading...</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-tabs">
        <button
          className={`tab-btn ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          My Profile
        </button>
        <button
          className={`tab-btn ${activeTab === 'cars' ? 'active' : ''}`}
          onClick={() => setActiveTab('cars')}
        >
          My Car Listings ({userCars.length})
        </button>
      </div>

      {successMessage && (
        <div className="success-message">{successMessage}</div>
      )}

      {activeTab === 'profile' ? (
        <div className="profile-section">
          <h2>Profile Settings</h2>
          {/* Profile form remains the same as before */}
        </div>
      ) : (
        <div className="cars-section">
          <h2>Manage Your Car Listings</h2>

          {userCars.length > 0 ? (
            <div className="user-cars-grid">
              {userCars.map((car) => (
                <div key={car._id} className="user-car-card">
                  <img src={car.image} alt={car.title} />
                  <div className="car-details">
                    <h3>{car.title}</h3>
                    <p><strong>Brand:</strong> {car.brand}</p>
                    <p><strong>Price:</strong> ${car.price}</p>
                    <p><strong>Status:</strong> 
                      <span className={`status ${car.isApproved ? 'approved' : 'pending'}`}>
                        {car.isApproved ? 'Approved' : 'Pending Approval'}
                      </span>
                    </p>
                  </div>
                  <div className="car-actions">
                    <button 
                      onClick={() => navigate(`/edit-car/${car._id}`)}
                      className="edit-btn"
                    >
                      Edit
                    </button>
                    <button 
                      onClick={() => handleDeleteCar(car._id)}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                    {!car.isApproved && user.isAdmin && (
                      <button
                        onClick={() => handleApproveCar(car._id)}
                        className="approve-btn"
                      >
                        Approve
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-cars">
              <p>You haven't listed any cars yet.</p>
              <button 
                onClick={() => navigate('/upload-car')}
                className="primary-btn"
              >
                List a New Car
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
