import React, { useEffect, useState, useContext } from "react";
import API from "../../utils/api";
import { AuthContext } from "../../context/AuthContext";
import "../../styles/cars.css"

const CarList = () => {
  const [cars, setCars] = useState([]);
  const { user } = useContext(AuthContext);

  const fetchCars = async () => {
    try {
      const res = await API.get("/cars");
      setCars(res.data);
    } catch (err) {
      console.error("Failed to fetch cars:", err);
      alert("Failed to load cars. Please try again later.");
    }
  };

  const handleDelete = async (carId) => {
    if (!window.confirm("Are you sure you want to delete this car?")) return;

    try {
      await API.delete(`/cars/${carId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      alert("Car deleted successfully!");
      fetchCars(); // Refresh the list
    } catch (err) {
      console.error("Failed to delete car:", err);
      alert("Error deleting car or unauthorized.");
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  // Debug output
  console.log("USER:", user);
  console.log("CARS:", cars);

  return (
    <body class="container">
        <div style={{ padding: "20px" }}>
      <h2 color="black">Shop Cars</h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
          gap: "20px",
        }}
      >
        {cars.length === 0 ? (
          <p>No cars found</p>
        ) : (
          cars.map((car) => (
            <div
              key={car._id}
              style={{
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
                position: "relative",
                background: "#fff",
              }}
            >
              <div
                style={{
                  position: "absolute",
                  top: 0,
                  right: 0,
                  background: "red",
                  color: "white",
                  padding: "2px 6px",
                  fontSize: "12px",
                }}
              >
                Sale!
              </div>
              {car.image && (
                <img
                  src={car.image}
                  alt={car.title}
                  style={{
                    width: "100%",
                    height: "180px",
                    objectFit: "cover",
                    borderRadius: "4px",
                  }}
                />
              )}
              <h3 style={{ margin: "10px 0 5px" }}>{car.title}</h3>
              <p>
                <strong>Brand:</strong> {car.brand}
              </p>
              <p>
                <strong>Model:</strong> {car.model}
              </p>
              <p>
                <span
                  style={{
                    textDecoration: "line-through",
                    color: "#888",
                  }}
                >
                  ${car.price + 500}
                </span>{" "}
                <strong style={{ color: "#e60000" }}>${car.price}</strong>
              </p>
              <p>
                <strong>Payment:</strong> {car.paymentType}
              </p>

              {/* Show Delete if user is admin or owns this car */}
              {(user?.id === car?.seller?._id || user?.isAdmin) && (
                <button className="btn delete-btn" onClick={() => handleDelete(car._id)}>
  Delete
                </button>
              )}
            </div>
          ))
        )}
      </div>
    </div>
    </body>
    
  );
};

export default CarList;
